node.code:
my( $user, $passwd, $salt, $svU )= @_;
my $sql =     sprintf( 'title=%s and passwd=%s',
        map $DB->quote($_), $user, $passwd );

my $proto= $DB->sqlSelectHashref(
    'title, passwd, realname, email',
    'newuser',
    $sql,
);

return   if  ! $proto  ||  ! $proto->{title};
my $new_id= $DB->insertNode(
    $proto->{title},
    $DB->getType('user'),
    -1,
    $proto,
);

my $NEWUSER= getNodeById( $new_id );
$NEWUSER->{author_user}= $new_id;
$DB->updateNode( $NEWUSER, -1 );

my $SETTINGS = getVars( $NEWUSER );
for (
    #[ htmlerror	=> 3 ],
    [ htmlnest	=> 'on' ],
    [ htmlpreview	=> 4 ],
    [ nullvote    => 'on' ],
    [ msgonreply  => 'on' ],
    [ preferred_theme => 1068 ], # Red Theme
) {
    $SETTINGS->{$_->[0]} = $_->[1];
};
setVars( $NEWUSER, $SETTINGS );

htmlcode('createscratchpad','',$new_id);

$HTMLVARS{newuser}= $new_id;

$DB->sqlInsert('emailpwd', { 
     user_id => $new_id,
     ip => $ENV{REMOTE_ADDR},
     email => $proto->{email},
     reason => 'New user'
});

$$svU= $NEWUSER;
