node.page:
[%
  my $altview = 0 + ! $q->param('normal_user_view');
  my $altlabel =
    $altview ? "Normal View" :
    $NODE->{type}{title} eq "faqlist" ? "SDC View" :
    $NODE->{type}{title} eq "tutlist" ? "Pedagogue View" :
    $NODE->{type}{title} eq "catqalist" ? "QandAEditors View" :
    "Abnormal View"; # "can't happen"

  qq[<p align="right"><small><b>]
  .linkNode(
          $NODE,
          'Edit',
          { displaytype => 'edit' }
   )
  ."<br /> "
  .linkNode(
          $NODE,
          $altlabel,
          { normal_user_view => $altview }
   )
  .qq[</b></small><p>] if canUpdateNode($USER,$NODE);
%]
[%
  htmlcode('parselinks','',
    'doctext',
    {
      form => 'method,action,enctype',
      input => 'type,name,value,size,/',
    },
  );
%]
<hr />
[{render_doclist_group}]
