node.page:
[%
    my $text= $NODE->{doctext};
    my $as= lc(($q->param("as"))[-1])
        || $VARS->{sourcetext_as} || "code";
    if(  "text" eq $as  ) {
        if(  "bare" eq $q->param("style")  ) {
            $CURRENTNODE->{parent_container}= '';
            return join '',
                $q->header("text/plain"),
                $text;
        }
        return join '',
            "<pre>\n",
            $q->escapeHTML( $text ),
            "\n</pre>\n";
    }
    if(  "html" eq $as  ) {
        $text= $q->escapeHTML( $text );
        $text =~ s/  / &nbsp;/g;
        $text =~ s/\t/&rarr;/g;
        $text =~ s!\n!<br />\n!g;
        return $text;
    }
    return htmlcode( 'wrapcode','', $text );
%]
