node.page:
[%
    use Everything::Experience;

    my $isGod= isGod($USER);
    my $isUser= "user" eq $NODE->{type}{title};
    return "You see none of your votes here.\n"
        if(  ! $isGod  &&  ! $isUser  );

    my $isSelf= getId($USER) == getId($NODE);
    my $votescast= $isSelf;
    $votescast= $query->param("votescast")
        if(  $isGod  );

    my $form= "";
    my @votes;
    {
        my $limit=  "";
        my $select= "vote_id";
        my $user=   "voter_user";
        my $order=  "desc";
        if(  $isUser  ) {
            $form= join "\n",
                htmlcode('openform'),
                $query->escapeHTML( 'Limit ([-][[skip,]max]):' ),
                $query->textfield( -name => 'votelimit', -size => 10 ),
            '';
            $form .= join "\n",
                $query->checkbox( "votescast", "", "1", "Votes cast" ),
                "<b>by</b> this user",
            ''
                if(  $isGod  );
            $form .= join "\n",
                $query->submit( "submit", "submit" ),
                "</form>\n";
            $limit= $query->param("votelimit") || "";
            $order= "asc"   if  $limit =~ s/^-//;
            $limit= "20"    if  $limit !~ /^(\s*\d+\s*,)?\s*\d{1,3}\s*$/;
            $limit= "limit $limit";
            if(  $votescast  ) {
                $select= "voter_user";
                $user=   "voted_user";
            } elsif(  $isGod  ) {
                $select= 'voted_user';
            } else {
                $select= join ' ',
                    'voter_user=', getId($USER), 'and voted_user';
            }
        }
        my $csr= $DB->sqlSelectMany(
            "vote_id, weight, votetime, ip, $user as user_id",
            "vote",
            join( ' ',
                $select, '=', getId($NODE),
            ),
            "order by votetime $order $limit",
        );
        while(  my $VOTE= $csr->fetchrow_hashref()  ) {
            push @votes, $VOTE;
        }
    }

    if(  0 == @votes  ) {
        return(
            ( ! $isUser )
                ?   '<i>None have cast their vote on this node</i>' :
            ( $isGod )
                ?   sprintf( '%s<br /><i>This user has %s no votes</i>',
                        $form,
                        ( $votescast ) ? "cast" : "received",
                    ) :
            ( $isSelf )
                ?   '<i>You have never voted.</i>'
                :   "<i>You have voted on none of this monk's nodes.</i>"
        );
    }

    my $rows= '';
    my $plus= 0;
    my $minus= 0;
    my $hilite= '';
    foreach my $vote ( @votes ) {
        $vote->{votetime}= htmlcode(
            'ParseTimeInString','',
            {   string => $vote->{votetime},
                format => 'short',
            },
        );
        $rows .= sprintf(
            "<tr%s>%s</tr>\n",
            $hilite= $hilite ? '' : ' class="highlight"',
            join "\n",
                map( sprintf( '<td%s>%s</td>', @$_ ),
                    ( $isUser )
                        ?  [ '', linkNode( $vote->{vote_id} ) ]
                        :  [ '', $vote->{ip} ],
                    ( $isGod || $isSelf )
                        ?  [ '', linkNode( $vote->{user_id} ) ]
                        :  (),
                    [ ' align="center"', $vote->{weight} ],
                    [ ' align="right"', $vote->{votetime} ],
                ),
        );
        if ( $vote->{weight} < 0 ) {
            $minus++;
        } else {
            $plus++;
        }
    }

    return join "\n",
        $form,
        "<table width='100%'><tr><th>",
        $isUser ? "Title" : "IP Address",
        ( $isGod || $isSelf ) ? "</th><th>User" : "",
        "</th><th>Vote</th>",
        "<th>Date and Time</th></tr>",
        $rows,
        "</table>",
        "Total of $plus ++ and $minus -- votes =",
        $plus-$minus,
    '';
%]
