node.page:
[{votehead}]
<p align="right">
   [% linkNode $NODE->{author_user} %] submitted a patch for
   [% linkNode( $NODE->{for_node} ) %]'s ["$NODE->{field}"] field 
   [% $q->small(linkNode( $NODE->{for_node},"(code)",{displaytype=>'viewcode'} ) ) %]
   <br />
   [%
      linkNode( $NODE, "Edit", {displaytype=>"edit"} ) . " - "
         if   canUpdateNode($USER,$NODE);
   %]
   [%
      linkNode(
         getNode("Patch lister","superdoc"),
         "Related patches", {
            'only_p.for_node' => $NODE->{for_node},
         } );
   %]
   [%
      " - " . linkNode( $NODE, "apply $NODE->{title}", {
         op=>'applypatch', patchnum=>getId($NODE),
      } )
         if   isGod($USER);
   %]
</p>[{voteit}]<p>
   <b>Reason:</b> [{parselinks:reason}]<br />
   <b>Last applied:</b>
   [%
      my $when= $NODE->{applied};
      $when= "(never)"   unless $when =~ /[1-9]/;
      $when.= " <b>By:</b> ".linkNode($NODE->{applied_by})
         if $NODE->{applied_by};
      return $when;
   %]
[%
   my $N = getNodeById( $NODE->{for_node} );
   my $Ndiff;

   if (my $against=$q->param('diff_against')) {
      $Ndiff=getNodeById( $against )||undef;
      $q->param('diff_against','') unless $Ndiff;
   } 

   my $no_diff = $q->param('no_diff');

   return "<h3>Access denied</h3>"
      if  ! canReadNode( $USER, $N );

   my $field = htmlcode('lookup_patchable_field', '', $N, \my %patchinfo);
   return "<h3>Illegal patch</h3>"
      if  ! isGod($USER) && ( $field ne $NODE->{field} || $patchinfo{nopatch} );

   my $old=$Ndiff ? $Ndiff->{code} : $N->{ $NODE->{field} };
   my $new=$NODE->{code};
   my $title=$Ndiff ? "Against " .
                      linkNode($Ndiff,$Ndiff->{node_id}) .
                      " the patch is" 
                    : '';

   my $html = '';

  if ( $no_diff ) {
   if ($NODE->{field} eq "vars") {
      $html .= htmlcode( 'displayvars', '', 'code' );
   } else {
      $html .= htmlcode( 'listcode', '', 'code' );
   }
  }
  else { 
   if ($NODE->{field} eq "vars") {
      my $diff = Everything::cmpVarsStrs( $old, $new );
      $html .=
         $q->p( $q->strong(
              ($title || "The Patch Is")
              . ($diff ? " Different" : " Current")
         ))
         . htmlcode('displayvars','', 'code', $diff ? Everything::unpackVars( $old ) : undef);
   } else {
      $html .=
          htmlcode('diff_strings','',
                   $old,$new,
                   {use_query=>1, title=>$title, diff_against=>$Ndiff->{node_id}||0 }
                  );
   }
  }

   $html .= htmlcode('shownote')
         .  htmlcode('votefoot')
         . '<hr /><b>Related Patches:</b><br />'
         . htmlcode('listpatches','',
                              { 
                                diff    => $NODE->{for_node},
                                old     => ($Ndiff ? getId($Ndiff) : 0),
                                new     => getId($NODE),
                             });

   return $html;

%]
