node.page:
[% if ($query->param("op") eq "new") {
	$$NODE{parent_node} = $query->param("lastnode_id");
	updateNode $NODE, $USER;

}
%]

[% listCode($NODE->{doctext}, 1) %]

[%
  if ($$NODE{parent_node}) {
	my $PARENT = selectNode ($$NODE{parent_node});
	return "<font size=2><p align=right>go back to " . linkNode ($PARENT) ."</font><br><p>";
}
%]

[%
my $str="<BR><BR>";
if(canUpdateNode($USER,$NODE) and $$USER{title} ne "Anonymous Monk"){
   $str.=htmlcode('openform','',-multipart=>1);
   $str.=$query->hidden("displaytype");
   $str.=$query->hidden("node_id", getId $NODE); 
   $str.="<BR>Title:<BR>";
   $str.=htmlcode('textfield',"title");
   $str.="<BR>Text:<BR>";
   $str.=htmlcode('textarea',"doctext");
   $str.=$query->submit('sexisgood', 'submit');
   $str.=$query->end_form."<BR><BR>";
}
$str;
%]
