<p align=right>
[%
if ($query->param('op') eq 'close') {
	$$VARS{group} = "";
	return "<SCRIPT language=\"javascript\">parent.close()</SCRIPT>";		
} 
$$VARS{group}||= getId ($NODE);
linkNode $NODE, "close", {displaytype=> $query->param('displaytype'),
	op => 'close'};
%]
[{groupeditor:form,1}]
