node.code:
return   unless  isGod($USER);

my $PATCH= $q->param('patchnum');
getRef( $PATCH );
return   unless  $PATCH;

my $N= getNodeById( $PATCH->{for_node} );
return   unless  $N;

$q->param( 'oldvalue', $N->{ $PATCH->{field} } );
$N->{ $PATCH->{field} }= $PATCH->{code};
updateNode( $N, $USER );

my $applier_id = getId($USER);

$DB->getDatabaseHandle()->prepare(
    "update patch set applied=now(), applied_by=? where patch_id=?"
)->execute( $applier_id, getId($PATCH) );

my $AUTHOR = getNodeById( $PATCH->{author_user} );
my $msg = "[id://$applier_id] has applied your patch [id://$PATCH->{node_id}].";
$DB->sqlInsert( "message", {
    msgtext => $msg,
    author_user => getId( getNode('root','user') ),
    for_user => $PATCH->{author_user},
} )
    if  $PATCH->{author_user} ne $applier_id
    &&  $USER->{nick} ne $AUTHOR->{title};

getNodeById( $PATCH, 'force' );

if ( $q->param('apply_mode') ) {
    $q->param( 'node_id',getId($PATCH));
    $q->param( 'displaytype', $q->param('apply_mode'));
} else {
    $q->param( 'node', 'patch completed page' );
    $q->param( 'type', 'strangedoc' );
    $q->param( 'node_id', '' );
}
