function toggleReply ( replyID )
{
  reply = document.getElementById( 'rb' + replyID );

  if ( ! reply ) {
    alert("Can't find reply ID " + replyID + "!");
  }

  if ( reply.style.display == 'none' ) {
    reply.style.display = 'block';
  } else {
    reply.style.display = 'none';
  }
}

function doNothing () { }
