data.field:
   my( $rowid )= @_;

   if ( $HTMLVARS{guest_user} == getId($USER) ) {
      return "No edit viewing for [Anonymous Monk], please login to view this";
   }

   my $N;
   {
      my $sth = $DB->sqlSelectMany(
         "*",
         "edithistory",
         "rowid = $rowid",
      );
      $N = $sth->fetchrow_hashref();
      $sth->finish();
   }

   if( $N->{private}  &&
       ! Everything::isApproved( $USER, getNode('editors','usergroup') ) ) {
      return 'This edit is marked private, sorry bud!';
   }

   my $html = '';

   $html .= qq[\n<p>On ]
      . htmlcode( 'parseTimeInString','', $N->{edittime} )
      . qq[, ]
      . linkNode( $N->{editor_user} )
      . qq[ edited the ']
      . $N->{fieldname}
      . qq[' field of ]
      . linkNode( $N->{edithistory_id} )
      . qq[.  You can see the ]
      . linkNode( $N->{edithistory_id}, "history of (non-owner) edits", {
            displaytype=>'edithistory',
            ($q->param('limit') ? ( limit => 0+$q->param('limit')) : ()),
        } )
      . qq[ to that node.\n</p><p>Changes:</p>];

   require Everything::Diff;
   Everything::Diff->import(qw( showDiff ));

   my $diff= showDiff( $N->{starttext}, $N->{endtext} );

   $diff =~ s#\n#<br />\n#g;
   $diff =~ s#  # &nbsp;#g;
   $html .= qq[<p>\n]
      . $diff
      . qq[\n</p>\n];

   return $html;
