data.field:
my ($opts)=@_;

my $user=($opts->{user}||=$USER);
my $vars=($USER->{user_id}==$user->{user_id}) ? $VARS : getVars($user);

my $NS= getVars(getNode('Newest Nodes Setting','setting'));

my %viewable;
foreach my $group ( 'all', split /,/, $NS->{specialgroups} ) {
   my $ok=$group eq 'all' ? 1 :
          $group eq 'gods' ? isGod($user) :
          Everything::isApproved($user,getNode( $group, 'usergroup' ) );
   foreach my $type (split /,/, $NS->{"nodetypes_$group"}||'') {
      $viewable{$type}||=$ok;
   }
}

my @legal = grep { (my $key = $_) =~ s/ /_/g; $NS->{$key} && $viewable{$_} }
            split /,/, $NS->{nodetypes};
my @types;

if ($q->param('nn_show_nodetypes')
    || $q->param('types')
    || (!exists($vars->{nn_hide_nodetypes}) && !$q->param('nn_hide_types'))
   ) {
    @types = grep { (my $key = $_) =~ s/ /_/g; $NS->{$key} && $viewable{$_} }
            split /,/, $opts->{nodetypes}
                         || $q->param('nn_show_nodetypes')
                         || $q->param('types')
                         || $vars->{nn_show_nodetypes}
                         || $NS->{nodetypes};
    my %has=map { $_ => 1 } @types;
    $opts->{hidden}=[grep !$has{$_},sort @legal];     
    $opts->{hide}={ map { $_ => 1 } @{$opts->{hidden}} };
} else {
    my %hide=map { $_ => 1 } split /,/,$q->param('nn_hide_types')
                                       ||$vars->{nn_hide_nodetypes};
    $opts->{hidden}=[sort keys %hide];
    $opts->{hide}=\%hide;
    @types=grep !$hide{$_},@legal;
}

foreach my $type (@legal) {
   (my $key = $type) =~ s/ /_/g;
   $opts->{typedata}{$type}=[split /,/,$NS->{$key}];
   $opts->{typedata}{$type}[1]||=$opts->{typedata}{$type}[0];
   $opts->{descr}{$type}=$opts->{typedata}{$type}[0]
}

$opts->{types}=\@types;
$opts->{typestr}=join ',',@types;

$opts->{legal}=\@legal;
$opts->{allowed}=\%viewable;
$opts->{setting}=$NS;


if ($opts->{set}) {
    if ($opts->{set} ne 'hide') {
        $vars->{nn_show_nodetypes}=$opts->{typestr}
    } else {
        $vars->{nn_hide_nodetypes}=join ",",@{$opts->{hidden}};
        delete $vars->{nn_show_nodetypes};
    }
}

return $opts->{typestr}
