data.field:
my( $field, $readmore_remove )= @_;

return htmlcode(
    'ParseLinksInContent','',
    $NODE->{$field},
    id => $NODE->{node_id},
    parsecode => 1,
    readmore_remove => $readmore_remove,
    no_dl => 1,
    nodetype => $NODE->{type}{title},
    overridefilter => 'override',
);
