data.field:
    my( $inner, $lastnodeid )= @_;

    my $HLS = getVars(getNode('handlelinks settings','setting'));

    if( $inner =~ m!^ \s* (\w+) :// \s* (.*?) \s* $ !x ){
        my $prefix = lc($1);
        my $suffix = $2;
        my $return;

        if ( $HLS->{$prefix} ) {
            my ( $nodeloc, $title ) =
                split( /\s*\|\s*/, $suffix, 2 );

            my $cpantitle = $title || $nodeloc;
            my $escapedname = $query->escape($nodeloc);

            $return = eval( $HLS->{$prefix} );
        }
        return $return || $q->escapeHTML("[$inner]");

    } elsif ( $inner=~/^\d+$/ ) {
        return linkNode( $inner );
    } else {
       return linkNodeTitle( $inner );
    }
