data.field:
[%
    return "Sorry, you can't see this because you aren't a developer."
        if  ! $DB->isApproved( $USER, 'pmdev' );

    my $field= htmlcode('lookup_patchable_field');

    if(  ! $field  ) {
        return join "\n",
            "<p><b>Sorry 'viewcode' means nothing for this nodetype.</b></p>",
            "<p>There doesn't appear to be any code associated directly",
            "with the nodetype of this node",
            "(" . linkNode( $NODE->{type} ) . ").",
            "If you think this is wrong please ask a",
            linkNode( getNode("gods",'usergroup'), "god" ),
            "to update the relevent setting.</p>",
            "<p>It is much more likely that the code you really",
            "want to see is in one of the following display",
            "pages that are used to render this nodetype:</p>",
            htmlcode('get_display_types'),
        '';
    }

    my $html= '';
    if(  'vars' ne $field  ) {
        $html .= htmlcode( 'listcode','', $field );
    } else {
        $html .= join "\n",
            htmlcode( 'displayvars','', $field ),
            htmlcode( 'open_new_patch_form','', 'vars', 'edit' ),
            $q->sethidden( "patch_code", $NODE->{vars} ),
            htmlcode( 'closeform','', 'Create Patch' ),
        '';
    }

    if(  'patch' ne $NODE->{type}{title}  ) {
        $html .= join "\n",
            "<br />",
            htmlcode('showsitedoclet'),
            htmlcode('listpatches'),
        '';
    } elsif(  'vars' ne $field  ) {
        $html .= join "\n",
            "<p>This text area allows you to cut/copy the patched text:",
            "<br /><form>",
            $q->textarea( 'code', $NODE->{$field}, 20, 60 ),
            "</form></p>",
        '';
    }

    return $html;
%]
