data.field:
[{showsitedoclet}]
<p>
Members:
<ul>
  [%
  return "<i>currently there are no members</i>\n" 
    unless $NODE->{group};
	
  my $ref = $NODE->{group};
  my $str;

  foreach (@$ref){ 
    my $N = selectNode $_;
    $str .= sprintf '<li>%s</li>', linkNode $N; 
  }

  $str;
  %]
</ul>
[%
  return sprintf '<p><a href="%s">Edit %s</a>',
    urlGen({ node => 'edit user group', group => $NODE->{title} }, -1), $NODE->{title}
      if isGod($USER);
"";
%]
