data.field:
[{parsecode:doctext}]
[%
my $GROUP=$$NODE{group};
my $str;
my $sectioncontainerid=getId(getType('sectioncontainer'));
my @sectionareas;
my @other;
foreach(@$GROUP){
   my $TEMP = $DB->getNodeById($_, 'light');
   if($$TEMP{type_nodetype}==$sectioncontainerid){
      push @sectionareas, $TEMP;
   }  else{
      push @other, $TEMP;
   }
}

        my $size=scalar @sectionareas;
        my $height=int($size/3);
        $height++ if $size%3;
        $str.=qq{<TABLE class="paneled" width="100%">};
        for(my $i=0; $i<$height; $i++){
           $str.="<TR>";
           for(my $j=0; $j<3; $j++){
               $str.="<TD>";
               if($sectionareas[$i+$height*$j]){
	          $str .= linkNode ($sectionareas[$i+$height*$j]);
               } else{
                 $str.="&nbsp;";
               }
               $str.="</TD>";
           }
           $str.="</TR>\n";
        }
        $str.="</TABLE>"; 
        $str.="<BR><BR>";
        if(@other){
           $str.="<UL>";
           for(@other){
               $str.="<LI>".linkNode($_)."\n";
           }
           $str.="</UL>";
        }
        if(isGod($USER)){
           $str.=htmlcode('windowview','editor,launch editor');
        }

#       if(canCreateNode($USER,getType("sitefaqlet"))){
#          $str.="<BR><A HREF="
#              .urlGen({
#                  node=>'New Faqlet',
#                  type=>'sitefaqlet',
#                  op=>'new',
#                  addToGroup=>getId($NODE) })
#              .">Add a faqlet to this area</A>";
#       }
    $str;
%]
