      ${\ linkNode(
          $NODE,
          'Edit',
          { displaytype => 'edit' }
        ) }
      </b></small><p>]
    if canUpdateNode($USER,$NODE);
%]
[%
  htmlcode ( 'ParseLinksInContent', '',
