data.field:
[%
  return "<i>No entries in this category.</i>"
    unless $NODE->{group};

  my $GROUP= $NODE->{group};
  my $str = qq(<table width="100%" border="0" cellpadding="2" cellspacing="0">);
  foreach my $id (reverse @$GROUP) {
    my $N= getNodeById($id);
    next  if ! $VARS->{showreaped}
          && $N->{author_user} == $HTMLVARS{NodeReaperID};
    my $date= htmlcode('parseTimeInString','', $N->{createtime});
    my $desc= htmlcode('ParseLinksInContent','',$N->{codedescription});
    $str.= qq(<tr class="post_head">)
    . "<td valign=top>" . linkNode($N) . "<br/>on $date</td>"
    . "<td valign=top>by " . linkNode($N->{author_user}) . "</td>"
    . qq(</tr><tr class="post_body">)
    . "<td colspan=2>$desc</td>"
    . "</tr>\n";
  }
  $str.= "</table>\n";
  $str.= htmlcode('windowview','editor,launch editor')
    if isGod($USER);
  return $str;
%]
