data.field:
[%
    my $author= getNodeById( $NODE->{author_user} );
    if(  '[NodeReaper]' eq $author->{nick}  ) {
        return join "\n",
            q{<p>Account locked</p>},
            q{<p>Please <a href="?node_id=491035">contact the site administrators</a>},
            q{ if you feel your account should be made available again.</p>},
        '';
    } elsif(    $HTMLVARS{guest_user} == getId($USER)
            &&  Everything::Experience::getLevel($NODE->{author_user}) < 4
    ) {
        return join "\n",
            q{<p>Anonymous users can't see the scratchpads of new monks.</p>},
            q{<p>Please <a href="?node_id=109">log in</a>},
            q{ if you want to view this scratchpad.</p>},
            q{<p>At Level 4, a monk's scratchpad becomes visible},
            q{to even anonymous visitors.</p>},
        '';
    } else {
        return htmlcode('handle_scratchpad_display');
    }
%]
