data.field:
[%
  return "Tough cookies!"
    if !$DB->canReadNode( $USER, $NODE) 
    or ($NODE->{type}{title} eq 'wiki' 
    and !htmlcode('hasWikiPermission','read',$NODE));

  #return unless $DB->isApproved($USER, getNode( 'editors', 'usergroup' ) );

  use lib $HTMLVARS{uselib};
  use Algorithm::Diff qw/diff/;

  my @edits;

  my $sth = $DB->getDatabaseHandle->prepare('
    SELECT   *
    FROM     edithistory
    WHERE    edithistory_id = ?
    ORDER BY edittime DESC
  ');

  $sth->execute( getId($NODE) );

  while ( my $edit = $sth->fetchrow_hashref ) {
    my $old = [ split /\n/, $edit->{starttext} ];
    my $new = [ split /\n/, $edit->{endtext} ];

    $edit->{diff} = diff( $old, $new );

    push @edits, $edit;
  }

  return $q->table(
    $q->Tr(
      $q->td( { valign => 'top' }, $q->b( 'Time' ) ),
      $q->td( { valign => 'top' }, $q->b( 'Editor' ) ),
      $q->td( { valign => 'top' }, $q->b( 'Field' ) ),
      $q->td( { valign => 'top' }, $q->b( 'Diff' ) )
    ),
    map {
      $q->Tr(
        $q->td( $_->{edittime} ),
        $q->td( linkNode( $_->{editor_user} ) ),
        $q->td( $_->{fieldname} ),
        $q->td(
          join '<p>---</p>', map {
            map {
              "<pre>\n$_\n</pre>"
            } map {
              sprintf '%s %03d: %s',
                ($_->[0] eq '+' ? '>' : '<'),
                $_->[1] + 1,
                $q->escapeHTML($_->[2])
            } @$_
          } @{ $_->{diff} }
        )
      )
    } @edits
  );
%]
