data.field:
[%

  return ""  unless $$USER{title} eq "vroom";
  my $dohelp = $query->param("dohelp");  
  my $txt = $query->param("helptext");

  if($dohelp eq "create")
  {
    $DB->sqlInsert("nodehelp", {nodehelp_id => $$NODE{node_id}, nodehelp_text => $txt});

    return "Help topic created!<br><br>";
  }
  
  if($dohelp eq "update")
  {
    $DB->sqlUpdate("nodehelp", {nodehelp_text => $txt}, "nodehelp_id=$$NODE{node_id}");
    return "Help topic updated!<br><br>";
  }

  return "";

%]

[%
  my $csr = $DB->sqlSelectMany("*", "nodehelp", "nodehelp_id=$$NODE{node_id}");
  my $str = "<p align=\"right\">Help for: ".linkNode($NODE)."</p><br>";
  my $nohelp = "<em><p align=\"center\">No help topic available for this item</p></em>";
  if(my $row = $csr->fetchrow_hashref())
  { 
     if(length($$row{nodehelp_text}) < 3)
     {
      $str.= $nohelp 
     }
     else
     {
      $str.= parseLinks($$row{nodehelp_text});
     }
  }
  else
  {
     $str.= $nohelp;
  }
  
  $str.= "<br><br>";
  return $str;
%]

[%
  return unless  $$USER{title} eq "vroom";

  my $csr = $DB->sqlSelectMany("*", "nodehelp", "nodehelp_id=$$NODE{node_id}");
  my $row;
  my $dohelp;
  if($row = $csr->fetchrow_hashref())
  {
    $dohelp = "update";
  }
  else
  {
    $dohelp = "create";
  }

  my $str = "<br><br><p align=\"center\"><hr width=\"200\"></p><br>Because you are spiffy, you can edit the help topic for ".linkNode($NODE).":<br><br>".htmlcode("openform")."<input type=\"hidden\" name=\"dohelp\" value=\"$dohelp\"><textarea name=\"helptext\" rows=\"30\" cols=\"80\">$$row{nodehelp_text}</textarea><br><input type=\"submit\" value=\"submit\"></form>";

  return $str;
%]
