data.field:
[%
 my $pinned_id=$q->param('pinned_id')||htmlcode('handle_node_pin');
 if ( $pinned_id ) {
     my $request={ node_id=> $pinned_id };
     my $pm_code=htmlcode('get_external_node','',$request);
     return "<b>No code to diff</b>\n".
            "<!--".do {require Data::Dumper; 
                       Data::Dumper::Dumper($request)}."-->"
         unless $pm_code;
     my $cf= $request->{code_fields};
     my $test_code= $NODE->{ $cf->{ $NODE->{type_nodetype} } };

     s/^\s*\n// for $pm_code,$test_code;

     return "<h2>Diff against Live Server ($request->{title})</h2>".
            htmlcode( 'diff_strings', '', $pm_code, $test_code );
  } else {
     return "Too new to do a diff! Establish a node pin first.\n";
  }
%]
