data.field:
[%
my $sth = $DB->getDatabaseHandle()->prepare("show create table `$NODE->{title}`")
    or return "-- prepare for table $NODE->{title} failed\n";
$sth->execute()
    or return "-- table $NODE->{title} doesn't exist\n";
my $rec= $sth->fetchrow_hashref();
$sth->finish();
$rec->{"Create Table"}."\n"
%]
