data.field:
[%
   return "selectcode not available for this node type\n"
      if htmlcode('lookup_patchable_field');
     my $field='doctext';
     my $tools="";
     if ($NODE->{type}{title} eq 'scratchpad' && 
         htmlcode('scratchpad_mode','',$NODE,\$tools) eq 'private') 
     {
       $field='privatetext'; 
       $tools.="<hr />\n";
     }
     $tools . htmlcode('selectcode',$field);
%]
