data.field:
[%

my @GROUP = @{ $NODE->{group}||[] };
my @html=isGod($USER) ? '<p>'.linkNode($NODE,'Edit Group',
                              {displaytype=>'edit'}).'</p>' 

                      : ();
unless ($NODE->{group}) {
   push @html,"<p><i>This nodegroup is empty</i></p>";
   return join '',@html;
} 

push @html,$q->start_table({
    class=>'groupdisplay',
    width=>'100%',
    # rules=>'rows',
    # frame=>'void',
    cellpadding=>1,
    # cellspacing=>0,
  }),
  '<col width="20%" span=1 align="left" />',
  '<col width="80%" span=1 align="left" />',
;

push @html,$q->Tr( $q->th({class=>'groupdisplay'}, [qw(Rank Node)] ));
my $pos=0;
foreach my $id (@GROUP) {
    my $node=getNodeById($id);
    push @html, $q->Tr($q->td([
       $pos++,
       linkNode($node).' <small>('.linkNode($node->{type}).')</small>',
    ]));
}
push @html,'</table>';
return join "\n",@html;
%]
