data.field:
[%
  local $NODELET = $NODE;
  my $nname = $NODE->{title};
  my $allowed_nodelets = htmlcode('allowed nodelets');
  unless ( ','.$allowed_nodelets.',' =~ /,$nname,/i ) {
    return qq(<div class="error">You are not authorized to invoke the '$nname' nodelet!</div>);
  }
  updateNodelet($NODE);
  $NODE->{nltext}
%]
