data.field:
[% unless ($$NODE{group}) {
	"<i>This nodeball is empty</i>";
} else {
	my $GROUP = $$NODE{group};
	my $str;
	foreach my $N  (@$GROUP) {
		$N = $DB->getNodeById($N, 'light');
		$str .= "<li>" . linkNode ($N) . "($$N{type}{title})";
	}
	$str;
}
%]
<p>[%
  htmlcode('windowview','editor,launch editor')
    if canUpdateNode( $USER, $NODE );
%]
<hr />
<p>[{displayvars}]

[{open_new_patch_form:vars,edit}]
[%
   return $q->hidden( -name=>"patch_code", -value=>$NODE->{vars}, -force=>1 );
%]
[{closeform:Create Patch}]
<br />
[{showsitedoclet}]
<hr />
<br />
[{listpatches}]
