data.field:
[%
  if(  "new" eq $query->param("op")  ) {
    $$NODE{parent_node}=
      $query->param("categorized answer_parent_node");
    updateNode( $NODE, -1 );
  }
  if(  $$NODE{parent_node}  ) {
    my $PARENT= selectNode( $NODE->{parent_node} );
    my $GPARENT= selectNode( $PARENT->{parent_node} );
    my $QA= getNode( 'Categorized Questions and Answers', 'superdoc' );
    return qq[<p align="left"><font size="2">]
        . linkNode($QA,"Q&A") . " &gt; "
        . linkNode($GPARENT) . " &gt; "
        . linkNode($PARENT)
        . qq[ contributed by ]
        . linkNode($NODE->{original_author})
        . qq[</font><p>];
  }
  return "";
%]
[%
  my $url = urlGen({ node_id=>getId($NODE), op=>'sopwify' });
  $DB->isApproved($USER,getNode('QandAEditors','usergroup'))
    ? qq[<p><b><a href=$url>SOPWify!</a></b></p>] : ''
%]
