data.field:
[%
  use Everything::NodeCache;
  use Data::Dumper;

  return 'Go Away' unless isGod($USER) 
    || Everything::isApproved( $USER, getNode('power users', 'usergroup') );

  if ( my $uid = $query->param('borg') ) {
    htmlcode(
      'borg',
      $uid,
      10 * 60,
      $query->param('silent') || 0
    );
  }

  if ( my $uid = $query->param('unborg') ) {
    htmlcode(
      'unborg',
      $uid
    );
  }

  my $BUN = getNode('borged users', 'setting');
  my $BU  = getVars( $BUN );

  my @users = ( [ 0, 'Select user' ] );

  if ( my $ou = $DB->{cache}->getCachedNodeByName('Other Users', 'nodelet') ) {
    while ( $ou->{nltext} =~ m!<a href="\S*/index\.pl\?node_id=(\d+)">([^<]+)</a>!gi ) {
      my ($uid, $title) = ($1, $2);
      $title =~ s/$/ */ if $BU->{$uid} > time();

      push @users, [ $uid, $query->unescapeHTML($title) ];
    }
  } else {
    my $sth = $DB->getDatabaseHandle->prepare('
      SELECT   user_id,
               title
      FROM     user LEFT JOIN node ON user_id = node_id
      WHERE    UNIX_TIMESTAMP(NOW()) - UNIX_TIMESTAMP(lasttime) < 180
      ORDER BY experience DESC
    ');

    $sth->execute;

    while ( my $r = $sth->fetchrow_arrayref ) {
      $r->[1] =~ s/$/ */ if $BU->{$r->[0]} > time();
      push @users, [ @$r ];
    }
  }

  my $borg = htmlcode('openform') . 
    $query->checkbox('silent') . '<br />' .
    $query->popup_menu(
      'borg',
      [ map { $_->[0] } @users ], 
      0,
      { map { $_->[0] => $query->escapeHTML($_->[1]) } @users }
  ) . htmlcode('closeform', 'borg');

  my $borged = [
    grep {
      ref eq 'HASH'
    } map {
      getNodeById($_, 'light')
    } grep {
      time() < $BU->{$_}
    } keys %$BU
  ];

  my $unborg = @$borged ? htmlcode('openform') . $query->popup_menu(
    'unborg',
    [ map getId($_), @$borged ],
    '',
    {
      map {
        getId($_) => sprintf '%s (%d)',
                      $query->escapeHTML($_->{title}),
                      $BU->{getId($_)} - time()
      } @$borged
    }
  ) . htmlcode('closeform', 'unborg') : '';

  my $type = $NODE->{type}{title};
  my $context = '';
  if($type eq 'user'){
    $context = join "<br />\n",
      linkNode( 174976, 'Shared IPs',         { un   => $NODE->{title} } ),
      linkNode( 209653, 'Uncanny Likenesses', { un   => $NODE->{title} } ),
      linkNode( 153985, 'Edit User Vars',     { user => $NODE->{title} } );
  }

  return qq[$borg $unborg $context];
%]
