#!/usr/bin/perl -w

use Net::IRC;
use strict;

my $irc = new Net::IRC;

my $conn = $irc->newconn(	

	Server 		=> shift || 'irc.azzurra.org',		
	Port		=> shift || '6667', 	
	Nick		=> 'Iper_',	
	Ircname		=> 'Only an IRC roBOT',	
	Username	=> 'Mr. Iper');
	$conn->{channel} = shift || '#prova' or die "Error: connection refused\n";


my @sentences = (

