sub on_connect {

	my $conn = shift;
	$conn->join($conn->{channel});
	$conn->privmsg($conn->{channel}, "Ladies and gentlemen, giulia_1 is here!");
	$conn->{connected} = 1;
}

sub on_join {

	my ($conn, $event) = @_;
	my $nick = $event->{nick};
	if($nick ne 'Iper_') {($conn->privmsg($conn->{channel}, "Hi, $nick!"));}
}	

sub on_invite {

	my ($conn, $event) = @_;
	my $nick = $event->{nick};
	$conn->privmsg($nick, "$nick, tnx for inviting me on your chan but now I'm very busy. See you later.");
}

sub on_part {

	my ($conn, $event) = @_;
	my $nick = $event->{nick};
	$conn->privmsg($conn->{channel}, "Bye, $nick!");
}

   

sub on_ping {

    my ($conn, $event) = @_;
    my $nick = $event->nick;
    $conn->ctcp_reply($nick, join (' ', ($event->args)));
    print "*** CTCP PING request from $nick received\n";
}

sub on_version {

    my ($conn, $event) = @_;
    my $nick = $event->nick;
    my $reply = "VERSION Binary os, Binary client, Athlon xp +infinite";
    $conn->ctcp_reply($nick, $reply);
    print "*** CTCP VERSION request from $nick received\n";
}

sub on_source {

    my ($conn, $event) = @_;
    my $nick = $event->nick;
    my $reply = "SOURCE ./query giulia_1 (or giulia_0 if she's away) or type !source";
    $conn->ctcp_reply($nick, $reply);
    print "*** CTCP SOURCE request from $nick received\n";
}

sub on_dcc {

    my ($conn, $event) = @_;
    my $nick = $event->nick;
    my $reply = "DCC no dcc, tnx";
    $conn->ctcp_reply($nick, $reply);
    $conn->privmsg($nick, "Hi $nick, I'm sorry but my client doesn't support dcc.");
    print "*** CTCP DCC request from $nick received\n";
}

sub on_msg {

	my ($conn, $event) = @_;
        my $nick = $event->nick;
        my $text = $event->{args}[0];

 	if ($text =~ /^\!(.+)$/) {

        if ($text =~ "source") {
        $conn->privmsg($nick, "$nick, I'm sending to you my source code ");
        $conn->schedule(10, \&Net::IRC::Connection::new_send, $nick, "iper.pl");
        print "*** Iper's source has been requested by $nick \n";}

        if ($text =~ "hi"){
        $conn->privmsg($nick, "Hi, $nick!");}

        if ($text =~ "quote"){
        $conn->privmsg($nick, &rand());}
      
        if ($text =~ "about"){
        $conn->privmsg($nick, "Internet Perl Eccentric Robot, a Giulia's project ==> Mr. Iper r0x! published on perlmonks.org");}

        if ($text =~ "help") {

        $conn->privmsg($nick, "My commands are: !hi, !quote, !about !source, !zena (on msg) or !quote, !go_out and !source (on public). I also say <<Hi, nick!>> or <<Bye, nick!>> (on join & on part), I reply to ping, version, source, dcc and invite. If the language  used is bad I say <<Nick, moderate the language plese>> and before exiting I says <<Bye...>> to the channel ...I'm a great bot :)");}

        if ($text =~ "zena") {

        $conn->privmsg($conn->{channel}, chr(3)."12,4FORZA".chr(3)."4,12GENOA"); }

                              }        

        else {

        $conn->privmsg($event->{nick}, "Don't anybody /msg IperBot in this way, contact giulia_1 for more informations or type !help for the  commands' list");

            }

}

sub on_public {

	my ($conn, $event) = @_;
        my $nick = $event->nick;
        my $text = $event->{args}[0];
        my ($arg) = ($event->args);

 	if ($text =~ /^\!(.+)$/) {

        if ($text =~ "source") {
        $conn->privmsg($nick, "$nick, I'm sending to you my source code ");
        $conn->schedule(10, \&Net::IRC::Connection::new_send, $nick, "iper.pl");
        print "*** Iper's source has been requested by $nick \n";}

        if ($text =~ "go_out") {
	$conn->privmsg($conn->{channel}, "Ok, $nick, I'm only a bot");
	$conn->quit("Quitted by $nick");
	exit 0;}

        if ($text =~ "quote"){
        $conn->privmsg($conn->{channel}, &rand());}

                             }

        if ($arg =~ /azz/i){
	
        $conn->privmsg($conn->{channel}, "$nick, moderate the language please!");

        }

   
              }

sub on_leaving {

	my ($conn, $event) = @_;
	$conn->privmsg($conn->{channel}, "Bye all! => RETN => C3 => 11000011");
}        

sub rand {

       return $sentences[ rand scalar @sentences ];
}


$conn->add_handler('join', \&on_join);
$conn->add_handler('invite', \&on_invite);
$conn->add_handler('part', \&on_part);
$conn->add_handler('cping', \&on_ping);
$conn->add_handler('cversion', \&on_version);
$conn->add_handler('csource', \&on_source);
$conn->add_handler('cdcc', \&on_dcc);
$conn->add_handler('msg',    \&on_msg);
$conn->add_handler('public', \&on_public);
$conn->add_handler('leaving', \&on_leaving);
$conn->add_handler('376', \&on_connect);

$irc->start();


